import React, { useState } from "react";
import CategoryBox from "./components/CategoryBox";
import { FaLaugh, FaGhost, FaChild, FaBolt } from "react-icons/fa";

function App() {
  const [selectedCategory, setSelectedCategory] = useState("");
  const [movies, setMovies] = useState([]);

  const categories = [
    { label: "Comedy", icon: FaLaugh },
    { label: "Horror", icon: FaGhost },
    { label: "PG", icon: FaChild },
    { label: "Action", icon: FaBolt },
  ];

  const fetchMovies = async (category) => {
    try {
      const response = await fetch(
        `http://localhost:5001/api/search?genre=${category}`
      );
      const data = await response.json();
      setMovies(data); // Update movies state
    } catch (error) {
      console.error("Error fetching movies:", error);
    }
  };

  const handleCategoryClick = (label) => {
    setSelectedCategory((prev) => (prev === label ? "" : label));
    if (selectedCategory !== label) {
      fetchMovies(label); // Fetch movies if a new category is selected
    }
  };

  return (
    <div className="App" style={styles.container}>
      <h1 style={styles.title}>Movie Recommender</h1>
      <div style={styles.categoriesRow}>
        {categories.map((category, index) => (
          <CategoryBox
            key={index}
            icon={category.icon}
            label={category.label}
            selected={selectedCategory === category.label}
            onClick={() => handleCategoryClick(category.label)}
          />
        ))}
      </div>
      <div style={styles.moviesList}>
        {movies.length > 0 ? (
          movies.map((movie) => (
            <div key={movie.id} style={styles.movieCard}>
              <img
                src={movie.poster}
                alt={movie.title}
                style={styles.moviePoster}
              />
              <h3>{movie.title}</h3>
              <p>{movie.release_date}</p>
            </div>
          ))
        ) : (
          <p style={styles.noMovies}>
            {selectedCategory ? "Loading movies..." : "Select a category to see recommendations."}
          </p>
        )}
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    minHeight: "100vh",
    backgroundColor: "#f9f9f9",
    textAlign: "center",
  },
  title: {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "2rem",
  },
  categoriesRow: {
    display: "flex",
    justifyContent: "space-evenly",
    alignItems: "center",
    width: "100%",
    maxWidth: "800px",
    marginBottom: "2rem",
  },
  moviesList: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    gap: "20px",
    marginTop: "20px",
  },
  movieCard: {
    width: "200px",
    textAlign: "center",
  },
  moviePoster: {
    width: "100%",
    borderRadius: "10px",
  },
  noMovies: {
    fontSize: "1.2rem",
    color: "#666",
  },
};

export default App;
