import React, { useState } from "react";
import CategoryBox from "./components/CategoryBox";
import { 
  FaLaugh,  // Comedy
  FaGhost,  // Horror
  FaChild,  // Family
  FaBolt,   // Action
  FaHeart,  // Romance
  FaGlobe,  // Adventure
  FaTheaterMasks, // Drama
  FaRunning,     // Thriller
  FaSpaceShuttle, // Sci-Fi
  FaDragon,      // Fantasy
  FaVideo,       // Animation
  FaMusic        // Musical
} from "react-icons/fa";



const [serverPort, setServerPort] = useState(5001);

useEffect(() => {
  // Try to connect to different ports if 5001 fails
  const tryPort = async (port) => {
    try {
      const response = await fetch(`http://localhost:${port}/api/search?genreId=28`);
      if (response.ok) {
        setServerPort(port);
        return true;
      }
    } catch (e) {
      return false;
    }
  };

  const findPort = async () => {
    for (let port = 5001; port <= 5010; port++) {
      if (await tryPort(port)) break;
    }
  };

  findPort();
}, []);


function App() {
  const [selectedCategory, setSelectedCategory] = useState("");
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const categories = [
    { label: "Comedy", icon: FaLaugh, id: "35" },
    { label: "Horror", icon: FaGhost, id: "27" },
    { label: "Family", icon: FaChild, id: "10751" },
    { label: "Action", icon: FaBolt, id: "28" },
    { label: "Romance", icon: FaHeart, id: "10749" },
    { label: "Adventure", icon: FaGlobe, id: "12" },
    { label: "Drama", icon: FaTheaterMasks, id: "18" },
    { label: "Thriller", icon: FaRunning, id: "53" },
    { label: "Sci-Fi", icon: FaSpaceShuttle, id: "878" },
    { label: "Fantasy", icon: FaDragon, id: "14" },
    { label: "Animation", icon: FaVideo, id: "16" },
    { label: "Musical", icon: FaMusic, id: "10402" }
  ];


  const fetchMovies = async (categoryId) => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(`http://localhost:${serverPort}/api/search?genreId=${categoryId}`);
      console.log('Fetching movies for category ID:', categoryId); // Debug log
      const url = `http://localhost:5001/api/search?genreId=${categoryId}`;
      console.log('Request URL:', url); // Debug log

      //const response = await fetch(url);

      if (!response.ok) {
        const errorText = await response.text();
        console.log('Error response:', errorText); // Debug log
        throw new Error(`HTTP error! status: ${response.status}, message: ${errorText}`);
      }

      const data = await response.json();
      console.log('Received data:', data); // Debug log
      setMovies(data);
    } catch (error) {
      console.error("Error fetching movies:", error);
      setError(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleCategoryClick = (category) => {
    console.log('Category clicked:', category); // Debug log
    const categoryObj = categories.find(cat => cat.label === category);
    console.log('Category object:', categoryObj); // Debug log

    setSelectedCategory((prev) => {
      const newSelected = prev === category ? "" : category;
      return newSelected;
    });

    if (categoryObj) {
      console.log('Fetching movies for category ID:', categoryObj.id); // Debug log
      fetchMovies(categoryObj.id); // Pass the ID, not the category name
    }
};

  const resetToHome = () => {
    setSelectedCategory("");
    setMovies([]);
    setError(null);
  };

  return (
    <div className="App" style={styles.container}>
      <h1
        style={styles.title}
        onClick={resetToHome}
        className="clickable-title"
        >
          Movie Recommender!
      </h1>
      <div style={styles.categoriesGrid}>
        {categories.map((category, index) => (
          <CategoryBox
            key={index}
            icon={category.icon}
            label={category.label}
            selected={selectedCategory === category.label}
            onClick={() => handleCategoryClick(category.label)}
          />
        ))}
      </div>
      <div style={styles.moviesList}>
        {isLoading && <p>Loading movies...</p>}
        {error && <p style={styles.error}>Error: {error}</p>}
        {!isLoading && !error && movies.length > 0 ? (
          movies.map((movie) => (
            <div key={movie.id} style={styles.movieCard}>
              <img
                src={movie.poster}
                alt={movie.title}
                style={styles.moviePoster}
              />
              <h3 style={styles.movieTitle}>{movie.title}</h3>
              <p style={styles.movieDate}>{movie.release_date}</p>
            </div>
          ))
        ) : (
          <p style={styles.noMovies}>
            {selectedCategory
              ? "No movies found."
              : "Select a category to see recommendations."}
          </p>
        )}
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    minHeight: "100vh",
    backgroundColor: "#f9f9f9",
    textAlign: "center",
  },
  title: {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "2rem",
  },
  categoriesRow: {
    display: "flex",
    justifyContent: "space-evenly",
    alignItems: "center",
    width: "100%",
    maxWidth: "800px",
    marginBottom: "2rem",
  },
  moviesList: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    gap: "20px",
    marginTop: "20px",
  },
  movieCard: {
    width: "200px",
    textAlign: "center",
  },
  moviePoster: {
    width: "100%",
    borderRadius: "10px",
  },
  noMovies: {
    fontSize: "1.2rem",
    color: "#666",
  },
  title: {
    fontSize: '3.5rem',
    fontWeight: 'bold',
    marginBottom: '2rem',
    fontFamily: "'Montserrat', sans-serif",
    background: 'linear-gradient(45deg, #2196F3, #1565C0)',
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent',
    cursor: 'pointer',
    transition: 'transform 0.2s ease-in-out',
  },
  categoriesGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(6, 1fr)', // Changed to 6 columns
    gap: '2rem',
    width: '100%',
    maxWidth: '1200px',
    marginBottom: '2rem',
    padding: '0 2rem'
  }
};

export default App;
