import React, { useState } from "react";
import CategoryBox from "./components/CategoryBox";
//import { FaLaugh, FaGhost, FaChild, FaBolt } from "react-icons/fa";

function App() {
  const [selectedCategory, setSelectedCategory] = useState("");

  const categories = [
    { label: "Comedy", icon: FaLaugh },
    { label: "Horror", icon: FaGhost },
    { label: "PG", icon: FaChild },
    { label: "Action", icon: FaBolt },
  ];

  const handleCategoryClick = (label) => {
    setSelectedCategory((prev) => (prev === label ? "" : label));
    // Fetch movies based on category here if needed
  };

  return (
    <div className="App">
      <h1 className="text-center text-3xl font-bold mb-8">Movie Recommender</h1>
      <div className="flex justify-evenly items-center border-b mb-8">
        {categories.map((category) => (
          <CategoryBox
            key={category.label}
            icon={category.icon}
            label={category.label}
            selected={selectedCategory === category.label}
            onClick={() => handleCategoryClick(category.label)}
          />
        ))}
      </div>
      <div className="content">
        {selectedCategory ? (
          <p className="text-center">Selected Category: {selectedCategory}</p>
        ) : (
          <p className="text-center">Select a category to see movies!</p>
        )}
      </div>
    </div>
  );
}

export default App;
