import React, { useState, useEffect } from 'react';

function App() {
  const [movies, setMovies] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');

  const categories = [
    { name: 'Horror', query: 'horror' },
    { name: 'Comedy', query: 'comedy' },
    { name: 'PG', query: 'family' },
    { name: 'Action', query: 'action' },
    {name: 'Thriller', query: 'thriller'}
    // Add more categories as needed
  ];

  const fetchMovies = (query) => {
    fetch(`http://localhost:5001/api/search?genre=${query}`)
      .then((res) => res.json())
      .then((data) => setMovies(data))
      .catch((err) => console.error('Error fetching movies:', err));
  };

  useEffect(() => {
    if (selectedCategory) {
      fetchMovies(selectedCategory);
    }
  }, [selectedCategory]);

  return (
    <div className="App">
      <h1>Movie Recommender!</h1>

      <div className="categories">
        {categories.map((category) => (
          <button
            key={category.query}
            className="category-button"
            onClick={() => setSelectedCategory(category.query)}
          >
            {category.name}
          </button>
        ))}
      </div>

      {movies.length > 0 ? (
        <ul>
          {movies.map((movie) => (
            <li key={movie.imdbID}>{movie.Title}</li>
          ))}
        </ul>
      ) : (
        <p>Loading movies...</p>
      )}
    </div>
  );
}

export default App;
